// Optional performance helper generated by Create React App
const reportWebVitals = onPerfEntry => {
	
  // Run only if a callback function was provided
  if (onPerfEntry && onPerfEntry instanceof Function) {
    import('web-vitals').then(
      ({ getCLS, getFID, getFCP, getLCP, getTTFB }) => {
		// Invoke the callback for each Core Web Vitals metric
		getCLS(onPerfEntry);
        getFID(onPerfEntry);
        getFCP(onPerfEntry);
        getLCP(onPerfEntry);
        getTTFB(onPerfEntry);
      }
    );
  }
};

export default reportWebVitals;
